<?phpclass MY_Controller extends CI_Controller {    public function __construct() {        parent::__construct();        $this->load->library('ion_auth');        // Login check        $exception_uris = array(            'auth/login',            'auth/logout',            'auth/forgot_password',        );        if (in_array(uri_string(), $exception_uris) == FALSE) {            if (!$this->ion_auth->logged_in()) {                // redirect them to the login page                redirect('auth/login', 'refresh');            }        } elseif (in_array(uri_string(), array('auth/login')) == TRUE) {            if ($this->ion_auth->logged_in()) {                // redirect them to the home page                redirect('/', 'refresh');            }        }        // Get the user's ID and add it to the config array        $config = array('user_id' => $this->session->userdata('user_id'));        // Load the ACL library and pass it the config array        $this->load->library('my_acl', $config);//        $this->my_acl->hasPermission('add_publication');    }}