<div class="row">    <div class="col-lg-12">        <h1 class="page-header">Company Master</h1>        <!--<a href="<?php // echo base_url() . "client/quick_add"; ?>" class="btn btn-primary pull-right btn-top-right" data-toggle="modal" data-target="#myModal" data-backdrop="static">Add Client</a>-->     </div><!-- /.col-lg-12 --></div><!-- /.row --> <div class="row">     <div class="col-lg-12">                <br />        <button class="btn btn-success" onclick="add_person()"><i class="glyphicon glyphicon-plus"></i> Add Company</button>        <button class="btn btn-default" onclick="reload_table()"><i class="glyphicon glyphicon-refresh"></i> Reload</button>        <br />        <br />        <table id="table" class="table table-striped table-bordered" cellspacing="0" width="100%">            <thead>                <tr>                    <th>name</th>                    <th>address</th>                    <th>phone</th>                    <th>fax</th>                    <th>email</th>                    <th style="width:125px;">Action</th>                </tr>            </thead>            <tbody>            </tbody>         </table>    </div><!-- /.col-lg-12 --> </div><!-- /.row --><script src="<?php echo base_url('assets/jquery/jquery-2.1.4.min.js')?>"></script><script type="text/javascript">var save_method; //for save method stringvar table;$(document).ready(function() {    //datatables    table = $('#table').DataTable({         "processing": true, //Feature control the processing indicator.        "serverSide": true, //Feature control DataTables' server-side processing mode.        "order": [], //Initial no order.        // Load data for the table's content from an Ajax source        "ajax": {            "url": "<?php echo site_url('company/ajax_list')?>",            "type": "POST"        },        //Set column definition initialisation properties.        "columnDefs": [        {             "targets": [ -1 ], //last column            "orderable": false, //set not orderable        },        ],    }); });function add_person(){    save_method = 'add';    $('#form')[0].reset(); // reset form on modals    $('.form-group').removeClass('has-error'); // clear error class    $('.help-block').empty(); // clear error string    $('#modal_form').modal('show'); // show bootstrap modal    $('.modal-title').text('Add Company'); // Set Title to Bootstrap modal title}function edit_person(id){    save_method = 'update';    $('#form')[0].reset(); // reset form on modals    $('.form-group').removeClass('has-error'); // clear error class    $('.help-block').empty(); // clear error string    //Ajax Load data from ajax    $.ajax({        url : "<?php echo site_url('company/ajax_edit/')?>/" + id,        type: "GET",        dataType: "JSON",        success: function(data)        {            $('[name="id"]').val(data.id);            $('[name="name"]').val(data.name);            $('[name="address"]').val(data.address);            $('[name="phone"]').val(data.phone);            $('[name="fax"]').val(data.fax);            $('[name="email"]').val(data.email);            $('[name="tax"]').val(data.tax);            $('[name="website"]').val(data.website)            $('#modal_form').modal('show'); // show bootstrap modal when complete loaded            $('.modal-title').text('Edit Company'); // Set title to Bootstrap modal title        },        error: function (jqXHR, textStatus, errorThrown)        {            alert('Error get data from ajax');        }    });}function reload_table(){    table.ajax.reload(null,false); //reload datatable ajax }function save(){    $('#btnSave').text('saving...'); //change button text    $('#btnSave').attr('disabled',true); //set button disable     var url;    if(save_method == 'add') {        url = "<?php echo site_url('company/ajax_add')?>";    } else {        url = "<?php echo site_url('company/ajax_update')?>";    }    // ajax adding data to database    $.ajax({        url : url,        type: "POST",        data: $('#form').serialize(),        dataType: "JSON",        success: function(data)        {            if(data.status) //if success close modal and reload ajax table            {                $('#modal_form').modal('hide');                reload_table();            }            $('#btnSave').text('save'); //change button text            $('#btnSave').attr('disabled',false); //set button enable         },        error: function (jqXHR, textStatus, errorThrown)        {            alert('Error adding / update data');            $('#btnSave').text('save'); //change button text            $('#btnSave').attr('disabled',false); //set button enable         }    });}function delete_person(id){    if(confirm('Are you sure delete this data?'))    {        // ajax delete data to database        $.ajax({            url : "<?php echo site_url('company/ajax_delete')?>/"+id,            type: "POST",            dataType: "JSON",            success: function(data)            {                //if success reload ajax table                $('#modal_form').modal('hide');                reload_table();            },            error: function (jqXHR, textStatus, errorThrown)            {                alert('Error deleting data');            }        });    }}function upload(){    alert('v');    $.ajaxFileUpload({            url             :"<?php echo site_url('comapny/upload_file')?>",             secureuri       :false,            fileElementId   :'image',            dataType        : 'json',             success : function (data, status)            {                if(data.status != 'error')                {                    $('#files').html('<p>Reloading files...</p>');                    //refresh_files();                     $('files').val('');                }                alert(data.msg);                $('#spimage').text(data.msg);            }        });        return false; }</script><!-- Bootstrap modal --><div class="modal fade" id="modal_form" role="dialog">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                <h3 class="modal-title">Company Form</h3>            </div>            <div class="modal-body form">                <form action="#" id="form" class="form-horizontal">                    <input type="hidden" value="" name="id"/>                     <div class="form-body">                        <div class="form-group">                            <label class="control-label col-md-3">Company Name</label>                            <div class="col-md-9">                                <input name="name" placeholder="Company Name" class="form-control" type="text">                                <span class="help-block"></span>                            </div>                        </div>                        <div class="form-group">                            <label class="control-label col-md-3">Address</label>                            <div class="col-md-9">                                <input name="address" placeholder="Address" class="form-control" type="text">                                <span class="help-block"></span>                            </div>                        </div>                         <div class="form-group">                            <label class="control-label col-md-3">PhoneNo</label>                            <div class="col-md-9">                                <input name="phone" placeholder="PhoneNo" class="form-control" type="text">                                 <span class="help-block"></span>                            </div>                        </div>                         <div class="form-group">                            <label class="control-label col-md-3">FaxNo</label>                            <div class="col-md-9">                                <input name="fax" placeholder="FaxNo" class="form-control" type="text">                                 <span class="help-block"></span>                            </div>                        </div>                         <div class="form-group">                            <label class="control-label col-md-3">EmailID</label>                            <div class="col-md-9">                                <input name="email" placeholder="EmailID" class="form-control" type="text">                                 <span class="help-block"></span>                            </div>                        </div>                         <div class="form-group">                            <label class="control-label col-md-3">TaxNo</label>                            <div class="col-md-9">                                <input name="tax" placeholder="TaxNo" class="form-control" type="text">                                 <span class="help-block"></span>                            </div>                        </div>                         <div class="form-group">                            <label class="control-label col-md-3">Website</label>                            <div class="col-md-9">                                <input name="website" placeholder="Website" class="form-control" type="text">                                 <span class="help-block"></span>                            </div>                        </div>                         <!--  <div class="form-group">                            <label class="control-label col-md-3">Logo upload</label>                            <div class="col-md-9">                                <!-- <input name="website" placeholder="Website" class="form-control" type="text">                                <input type="file" name="image" id="image" size="20" /> <br/>                                <button type="button" id="btnupload" onclick="upload()" class="btn btn-primary btn-sm">upload</button>                                <span name="spimage" id="spimage" style="display:none;"/>                            </div>                        </div> -->                     </div>                </form>            </div>            <div class="modal-footer">                <button type="button" id="btnSave" onclick="save()" class="btn btn-primary">Save</button>                <button type="button" class="btn btn-danger" data-dismiss="modal">Cancel</button>            </div>        </div><!-- /.modal-content -->    </div><!-- /.modal-dialog --></div><!-- /.modal --><!-- End Bootstrap modal --> 