function loadRO(obj) {    var vendor_id = obj.value;    $("#release_orders").html("<div class='form-group'>Loading...</div>");    $.ajax({        url: BASE_URL + "vendor/find_release_orders_html/" + vendor_id,        dataType: 'json',        success: function (response) {            if (response.result == "success") {                $("#release_orders").html(response.html);//                $('.hide_block').show();            } else {                $("#release_orders").html(response.html);            }        }    });}function loadInvoice(obj) {    var client_id = obj.value;    $("#release_orders").html("<div class='form-group'>Loading...</div>");    $.ajax({        url: BASE_URL + "client/find_invoices_html/" + client_id,        dataType: 'json',        success: function (response) {            if (response.result == "success") {                $("#release_orders").html(response.html);//                $('.hide_block').show();            } else {                $("#release_orders").html(response.html);            }        }    });}function paymentMode(mode) {    if (mode === 'cash') {        $('input[name=ref_no]').attr('disabled', 'disabled');    } else {        $('input[name=ref_no]').removeAttr('disabled');    }}$(document).on('change', '#invoice', function () {    var invoice_id = $(this).val();    $("#invoice_details").html("<div class='form-group'>Loading...</div>");    $.ajax({        url: BASE_URL + "payment/get_invoice_details_html/" + invoice_id,        dataType: 'html',        success: function (response) {            if (response == "0") {                $("#invoice_details").html('<p class="text-danger">Please select invoice</p>');                $('.hide_block').hide();            } else if(response == "1") {                $("#invoice_details").html('<p class="text-danger">No pending payments for this client</p>');                $('.hide_block').hide();            } else {                $("#invoice_details").html(response);                $('.hide_block').show();                            }        }    });});$(document).on('change', '#release_order', function () {    var invoice_id = $(this).val();    $("#release_order_details").html("<div class='form-group'>Loading...</div>");    $.ajax({        url: BASE_URL + "payment/get_release_order_details_html/" + invoice_id,        dataType: 'html',        success: function (response) {            if (response == "0") {                $("#release_order_details").html('<p class="text-danger">Please select release order</p>');                $('.hide_block').hide();            } else if(response == "1") {                $("#release_order_details").html('<p class="text-danger">No pending payments for this vendor</p>');                $('.hide_block').hide();            } else {                $("#release_order_details").html(response);                $('.hide_block').show();            }        }    });});